client_script:
$('main').addClass('card college-card');
$('.awesomplete>ul').css('position', 'static');

frappe.web_form.after_load = () => {
    frappe.web_form.set_value('created_via_web_form', 1);
}

frappe.web_form.after_save = () => {
    frappe.msgprint({
        message: "College was registered successfully!",
        title: "Success",
        indicator: "green"
    });
    setTimeout(() => {
	    window.location.replace("/colleges");	
	}, 3000);
}

frappe.web_form.cancel = () => {
    window.location.replace('/colleges');
}

custom_css:
.awesomplete .input-with-feedback {
    background-color: #F4F5F6 !important;
}

.page_content {
    margin-top: 10px;
}

@media (min-width: 768px) {
    .college-card {
        width: 50%;
        padding: 30px !important;
        margin-bottom: 30px !important;
    }
}

@media (max-width: 767px) {
    .college-card {
        padding: 35px !important;
        margin-bottom: 30px !important;
    }
}
