client_script:
$('main').addClass('card voting-card');
$('.awesomplete>ul').css('position', 'static');


// Populate college field
const collegeName = decodeURIComponent(window.location.hash.substr(1));
frappe.web_form.set_value("college", collegeName);

document.getElementsByTagName("input")[1].maxLength = 10;
document.getElementsByTagName("input")[1].minLength = 10;

frappe.web_form.after_load = () => {
    frappe.call({
        method: "frappe.client.get_value",
        args: {
            "doctype": "Vote",
            "filters": {"voter": frappe.session.user},
            "fieldname": "name"
        },
        callback: function(r) {
            if (!r.exc) {
                if (r.message.name) {
                    frappe.msgprint("You have already Voted!");
					window.location.replace("/leaderboard");
				}
            }
        }
    });
}

frappe.web_form.after_save = () => {
    window.location.replace('/leaderboard');
}

custom_css:
.awesomplete .input-with-feedback {
    background-color: #F4F5F6 !important;
}

.page_content {
    margin-top: 10px;
}

@media (min-width: 768px) {
    .voting-card {
        width: 50%;
        padding: 30px !important;
        margin-bottom: 30px !important;
    }
}

@media (max-width: 767px) {
    .voting-card {
        padding: 35px !important;
        margin-bottom: 30px !important;
    }
}
